<?php

namespace AppBundle\Entity;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\NoResultException;
use AppBundle\Module\Youtube\ChannelSelector;
use AppBundle\Module\Youtube\Template;

/**
 * SharedVideoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SharedVideoRepository extends EntityRepository
{
    public function getByProgram($program)
    {
        $programRegex = '~^[A-Z]{4}$~';
        if (preg_match($programRegex, $program)) {
            $query = $this->getEntityManager()
                          ->createQuery(
                           'SELECT v, p FROM HopeMqBundle:SharedVideo v
                             JOIN v.program p
                             WHERE p.code = :program
                             ORDER BY v.publish ASC')
                          ->setParameter('program', $program);
        } else {
            $query = $this->getEntityManager()
                          ->createQuery(
                           'SELECT v FROM HopeMqBundle:SharedVideo v
                             WHERE v.program = :program
                             ORDER BY v.publish ASC')
                          ->setParameter('program', $program);
        }

        $videos = $query->getResult();
        return $videos;
    }

    public function getByCode($code)
    {
        $query = $this->getEntityManager()
                      ->createQuery(
                       'SELECT v FROM HopeMqBundle:SharedVideo v
                         WHERE v.code = :code')
                      ->setMaxResults(1)
                      ->setParameter('code', $code);
        /**
         * @var \AppBundle\Entity\SharedVideo $video
         */
        try {
            $video = $query->getSingleResult();
        } catch(NoResultException $e) {
            $video = new SharedVideo();
        }

        return $video;
    }

    public function getForTrash($checked)
    {
        $programRepo = $this->getEntityManager()->getRepository('HopeMqBundle:SharedProgram');
        $mh = $programRepo->findOneBy(['code' => 'MHKU']);

        $query = $this->getEntityManager()->createQuery(
                       'SELECT v FROM HopeMqBundle:SharedVideo v
                         WHERE v.id NOT IN (:checked)
                           AND v.program = :program
                           AND v.trash = 0')
                      ->setParameter('checked', $checked)
                      ->setParameter('program', $mh);

        $videos = $query->getResult();
        return $videos;
    }
}
