<?php

namespace AppBundle\Entity;

use AppBundle\Media\Media;
use AppBundle\Media\Image;
use AppBundle\Helper\ListParameters;

/**
 * ShowRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ShowRepository extends ResourceRepository
{
    /**
     * @param ListParameters $params
     *
     * @return \Knp\Component\Pager\Pagination\PaginationInterface
     */
    public function getList(ListParameters $params)
    {
        $qb = $this->getEntityManager()->createQueryBuilder();
        $qb->select('s')
            ->from('AppBundle:Show', 's')
            ->orderBy('s.code', 'ASC');

        $shows = $this->getPaginator()->paginate($qb, $params->getPage(), $params->getLimit());

        foreach ($shows as $show) {
            $this->injectMedia($show);
        }

        return $shows;
    }

    /**
     * @param $code
     *
     * @return Show
     */
    public function getOne($code)
    {
        /**
         * @var \AppBundle\Entity\Show $show
         */
        $show = $this->findOneBy(['code' => $code]);
        if ($show !== null) {
            $this->injectMedia($show);
        }

        return $show;
    }

    private function injectMedia(Show $show)
    {
        $media = new Media();

        $image = new Image();
        $image->setUrl(sprintf('http://hope.ua/images/programs/%s.png', $show->getCode()));

        $media->setImage($image);

        $show->setMedia($media);
    }
}
